    //the derivative of the sigmoid function defined for x
    public static double sigmoidDeriv(double x) {
        return Math.pow(Math.E, x) / Math.pow((Math.pow(Math.E, x) + 1), 2);
    }

    //plots the input n on a sigmoid graph
    public static double sigmoid(double n){
        return(1 / (1 + Math.pow(Math.E, (-1*n))));
    }

    // finds highest closing price from start to day n
    public static double highest(int n) {
        double max = CL[0];
        for(int i = 0; i < n; i++) {
            if(CL[i] > max)
                max = CL[i];
        }
        return(max);
    }

    // finds lowest closing price from start to day n
    public static double lowest(int n) {
        double max = CL[0];
        for(int i = 0; i < n; i++) {
            if(CL[i] < max)
                max = CL[i];
        }
        return(max);
    }

    //finds EMA for day n
    public static double exponentialMovingAvg(double x, double t, int n) {
        n++;
        return((2/(t+1)) * x + (1-(2/(t+1))) * EMA[n-1]);
    }

    //finds ACD for day n
    public static double accumulationDistribution(int n) {
        n++;
        return(ACD[n-1] + VO[n] * ((CL[n]-LO[n])-(HI[n]-CL[n]) / (HI[n]-LO[n])));
    }

    //finds MACD for day n
    public static double movAvgConverDiver(int n) {
        return(exponentialMovingAvg(CL[n], 12, n) - exponentialMovingAvg(CL[n], 26, n));
    }

    //finds CO based on day n
    public static double chaikinOcillator(int n) {
        return(exponentialMovingAvg(AD[n], 3, n) - exponentialMovingAvg(AD[n], 10, n));
    }

    //finds VPT for day n
    public static double volumePriceTrend(int n) {
        n++;
        return(VPT[n-1] + VO[n] * ((CL[n]-CL[n-1])/CL[n-1]));
    }

    //finds current RSI (no way to find previous avgUPC and DPC values)
    public static double relativeStrengthIndex() {
        return(100 - (100/1+(avgUPC/avgDPC)));
    }

    // finds the momentum of the stock in based on closing prices from previous 10 days to day n
    public static double momentum(int n) {
        return(CL[n] - CL[n-10]);
    }

    //finds the price rate of change on day n (can't be less than 13)
    public static double priceROC(int n) {
        return(((CL[n]-CL[CL.length-(n+13)]) / CL[CL.length-(n+13)]) * 100);
    }

    //finds the volume rate of change on day n (can't be less than 13)
    public static double volumeROC(int n) {
        return(((VO[n]-VO[VO.length-(n+13)]) / VO[VO.length-(n+13)]) * 100);
    }

    //finds OBV on day n
    public static double onBalanceVolume(int n) {
        n++;
        if(CL[n] >= CL[n-1])
            return(OBV[n-1] + VO[n]);
        else
            return(OBV[n-1] - VO[n]);
    }

    //all for day n
    public static double getHI(int n) {
        return HI[n];
    }
    public static double getLO(int n) {
        return LO[n];
    }
    public static double getCL(int n) {
        return CL[n];
    }
    public static double getOP(int n) {
        return OP[n];
    }
    public static double getAD(int n) {
        return AD[n];
    }
    public static double getVO(int n) {
        return VO[n];
    }
    public static double getAvgSent(int n) {
        return avgSENT[n];
    }
    public static double getAvgVO() {
        return avgVO;
    }
    public static double getExpectedResult(int n) {
        return expectedResults[n];
    }
